import React from "react";
import { StatusBar } from "expo-status-bar";

import {
  StyledContainer,
  InnerContainer,
  PageLogo,
  PageTitle,
  SubTitle,
  S
} from "./../components/styles";

const Login = () => {
  return (
    <StyledContainer>
      <StatusBar style="dark" />
      <InnerContainer>
        <PageLogo
          resizeMode="cover"
          source={require("./../assets/img/img1.png")}
        />
        <PageTitle>Basalon Shel Salomon</PageTitle>
      </InnerContainer>
    </StyledContainer>
  );
};

export default Login;
